mapper.select:
SELECT id ,user_id ,category_id ,title ,content ,image_urls ,view_count ,created_at FROM communities WHERE 1=1
AND title LIKE CONCAT('%', #{title}, '%')
AND deleted_at IS NULL ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE communities
   SET user_id = #{userId}
       ,category_id = #{categoryId}
       ,property_id = #{propertyId}
       ,title = #{title}
       ,content = #{content}
       ,image_urls = string_to_array(#{imageUrls}, ',')
       ,updated_at = NOW()
 WHERE id = #{id}

UPDATE communities
   SET deleted_at = NOW()
 WHERE id = #{id}
   AND user_id = #{userId}
   AND deleted_at IS NULL
