mapper.update:
UPDATE communities
   SET user_id = #{request.userId}
       ,category_id = #{request.categoryId}
       ,property_id = #{request.propertyId}
       ,title = #{request.title}
       ,content = #{request.content}
       ,image_urls = string_to_array(#{request.imageUrls}, ',')
       ,updated_at = NOW()
 WHERE id = #{id}

UPDATE communities
   SET deleted_at = NOW()
 WHERE id = #{id}
   AND user_id = #{request.userId}
   AND deleted_at IS NULL
