mapper.select:
SELECT id
       ,user_id
       ,target_id
       ,target_type
       ,reaction_type
       ,created_at
  FROM reactions
 WHERE user_id = #{userId}
   AND target_id = #{targetId}
   AND target_type = #{targetType}

mapper.update:
UPDATE reactions
   SET reaction_type = #{reactionType}
 WHERE user_id = #{userId}
   AND target_id = #{targetId}
   AND target_type = #{targetType}

mapper.select:
SELECT COUNT(*)
  FROM reactions
 WHERE target_id = #{communityId}
   AND target_type = #{targetType}
   AND reaction_type = 'LIKE'

SELECT COUNT(*)
  FROM reactions
 WHERE target_id = #{communityId}
   AND target_type = #{targetType}
   AND reaction_type = 'DISLIKE'

SELECT target_id ,COUNT(*) FILTER (WHERE reaction_type = 'LIKE') AS like_count ,COUNT(*) FILTER (WHERE reaction_type = 'DISLIKE') AS dislike_count FROM reactions WHERE target_type = #{targetType} AND target_id IN
#{id}
GROUP BY target_id
